"use strict";(()=>{console.log("Individual classes logic for teacher");MemberStack.onReady.then(function(t){t.loggedIn||window.location.replace(window.location.hostname);let c=window.location.href.split("?classID=")[1];fetch(`https://apguru-server.herokuapp.com/api/v1/class/${c}`).then(o=>o.json()).then(o=>{let n=document.querySelectorAll(".class-topic-wrap");document.querySelector(".class-name-heading").innerHTML=o.className.split("-")[0],document.querySelector(".class-date-subheading").innerHTML=o.momentDate,document.querySelector(".class-location-subheading").innerHTML=o.classLocation,o.zoomLink?document.querySelector(".button-zoom-link").href=o.zoomLink:document.querySelector(".button-zoom-link").style.display="none",o.zoomRecording?document.querySelector(".button-zoom-recording").href=o.zoomRecording:document.querySelector(".button-zoom-recording").style.display="none",n.forEach(e=>{o.topicsCompleted&&o.topicsCompleted.includes(e.querySelector(".topic-id").innerHTML)&&(e.style.display="block")})})});})();
